INSERT INTO  banda(nome, data_fundacao, descricao, genero1) VALUES
			('Erroll Garner', '1923-06-23', 'Erroll Louis Garner (June 15, 1923 – January 2, 1977; some sources say b. 1921) was an American jazz pianist and composer known for his swing playing and ballads.', 'Jazz');
INSERT INTO  banda(nome, data_fundacao, descricao, genero1, genero2) VALUES
			('Pink Floyd', '1965-01-01', 'Pink Floyd were an English rock band formed in London in 1965. They achieved international acclaim with their progressive and psychedelic music.', 'Progressive', 'Psychedelic');
INSERT INTO  banda(nome, data_fundacao, descricao, genero1, genero2) VALUES
			('The Doobie Brothers', '1969-01-01', 'The Doobie Brothers are an American rock band from San Jose, California. The group has sold more than 40 million albums worldwide throughout its career. The band has been active for five decades, with their biggest success occurring in the 1970s.', 'Soul', 'Rock');
INSERT INTO  banda(nome, data_fundacao, descricao, genero1, genero2) VALUES
			('The Beatles', '1960-01-01', 'The Beatles were an English rock band formed in Liverpool in 1960. With members John Lennon, Paul McCartney, George Harrison and Ringo Starr, they became widely regarded as the foremost and most influential act of the rock era.', 'Pop', 'Rock');
INSERT INTO  banda(nome, data_fundacao, descricao, genero1, genero2) VALUES
			('Frank Zappa', '1940-12-21', 'Frank Vincent Zappa (December 21, 1940 – December 4, 1993) was an American musician, composer, activist and filmmaker. His work was characterized by nonconformity, free-form improvisation, sound experiments, musical virtuosity, and satire of American culture.', 'Experimental', 'Jazz');
INSERT INTO  banda(nome, data_fundacao, descricao, genero1) VALUES
			('Metallica', '1981-01-01', 'Metallica is an American heavy metal band from Los Angeles, California. The band was formed in 1981 by drummer Lars Ulrich and vocalist/guitarist James Hetfield. The band''s fast tempos, instrumentals and aggressive musicianship made them one of the founding "big four" bands of thrash metal, alongside Megadeth, Anthrax and Slayer.', 'Metal');
INSERT INTO banda(nome, data_fundacao, descricao, genero1, genero2) VALUES
			('Lou Reed', '1942-03-02', 'Lewis Allan Reed (March 2, 1942 – October 27, 2013) was an American musician, singer and songwriter. He was the guitarist, singer, and principal songwriter for the rock band the Velvet Underground, with a solo career that spanned five decades.', 'Rock', 'Experimental');